import React, { useState } from 'react';

const text = "Free books and drinks! Contact 83219032 for RSVP or contact 81230913 for further information!";

const censorLastDigits = (phoneNumber, censored) => {
  if (censored) {
    const firstFourDigits = phoneNumber.slice(0, 4);
    const censoredDigits = phoneNumber.slice(4).replace(/\d/g, '*');
    return firstFourDigits + censoredDigits;
  } else {
    return phoneNumber;
  }
};

const App = () => {
  const [clickedNumber, setClickedNumber] = useState(null);

  const handleClick = (phoneNumber) => {
    setClickedNumber(phoneNumber);
  };

  const formattedText = text.split(/(\d{8,})/).map((part, index) => {
    if (index % 2 === 1) {
      const phoneNumber = part;
      const isClicked = phoneNumber === clickedNumber;
      const uncensoredNumber = censorLastDigits(phoneNumber, false);
      const censoredNumber = censorLastDigits(phoneNumber, isClicked);

      return (
        <React.Fragment key={index}>
          <a
            href={`tel:${uncensoredNumber}`}
            onClick={() => handleClick(phoneNumber)}
          >
            {censoredNumber}
          </a>
        </React.Fragment>
      );
    } else {
      return <React.Fragment key={index}>{part}</React.Fragment>;
    }
  });

  return (
    <div>
      <p>{formattedText}</p>
    </div>
  );
};

export default App;
