import React from 'react';

const text = "Free books and drinks! Contact 83219032 for RSVP or contact 81230913 for further information!";

const censorPhoneNumber = (phoneNumber) => {
  const visibleDigits = phoneNumber.slice(-4); // Keep the last four digits visible
  const censoredDigits = '*'.repeat(phoneNumber.length - 4); // Censor the rest
  return censoredDigits + visibleDigits;
};

const App = () => {
  const formattedText = text.replace(/(\d{8,})/g, (match, phoneNumber) => {
    const censoredNumber = censorPhoneNumber(phoneNumber);
    return `<a href="tel:${phoneNumber}">${censoredNumber}</a>`;
  });

  return (
    <div>
      <p dangerouslySetInnerHTML={{ __html: formattedText }} />
    </div>
  );
};

export default App;
