import React, { useState } from 'react';

const text = "Free books and drinks! Contact 83219032 for RSVP or contact 81230913 for further information!";

const censorLastDigits = (phoneNumber, censored) => {
  if (censored) {
    const firstFourDigits = phoneNumber.slice(0, 4);
    const censoredDigits = phoneNumber.slice(4).replace(/\d/g, '*');
    return firstFourDigits + censoredDigits;
  } else {
    return phoneNumber;
  }
};

const PhoneNumber = ({ number }) => {
  const [censored, setCensored] = useState(true);

  const handleClick = () => {
    setCensored(!censored);
  };

  const uncensoredNumber = censorLastDigits(number, false);
  const censoredNumber = censorLastDigits(number, censored);

  return (
    <a href={`tel:${uncensoredNumber}`} onClick={handleClick}>
      {censoredNumber}
    </a>
  );
};

const App = () => {
  const phoneNumberRegex = /\d{8,}/g;
  const parts = text.split(phoneNumberRegex);

  return (
    <div>
      {parts.map((part, index) => (
        index % 2 === 1 ? (
          <PhoneNumber key={index} number={part} />
        ) : (
          <React.Fragment key={index}>{part}</React.Fragment>
        )
      ))}
    </div>
  );
};

export default App;
