import React, { useState } from 'react';

const text = "Free books and drinks! Contact 83219032 for RSVP or contact 81230913 for further information!";

const CensoredPhoneLink = ({ phoneNumber }) => {
  const [showUncensored, setShowUncensored] = useState(false);

  const handleClick = () => {
    setShowUncensored(!showUncensored);
  };

  const displayNumber = showUncensored ? phoneNumber : censorLastDigits(phoneNumber);

  return (
    <a href={`tel:${phoneNumber}`} onClick={handleClick}>
      {displayNumber}
    </a>
  );
};

const censorLastDigits = (phoneNumber) => {
  const firstFourDigits = phoneNumber.slice(0, 4);
  const censoredDigits = phoneNumber.slice(4).replace(/\d/g, '*');
  return firstFourDigits + censoredDigits;
};

const App = () => {
  const formattedText = text.replace(/(\d{8,})/g, (match, phoneNumber) => (
    `<CensoredPhoneLink phoneNumber="${phoneNumber}" />`
  ));

  return (
    <div>
      <p dangerouslySetInnerHTML={{ __html: formattedText }} />
    </div>
  );
};

export default App;
