import React, { useState } from 'react';
import './styles.css';

function CensoredPhoneNumber({ description }) {
  const [showUncensored, setShowUncensored] = useState(false);

  const handlePhoneClick = () => {
    setShowUncensored(!showUncensored);
  };

  const regex = /(\b\d{4}\s*)\d+/g;
  const censoredDescription = description.replace(regex, (match, group) => `${group}XXXX`);
  const uncensoredDescription = description.replace(regex, (match, group) => `${group}XXXX`);

  return (
    <div>
      {showUncensored ? (
        <span>{uncensoredDescription}</span>
      ) : (
        <span onClick={handlePhoneClick}>{censoredDescription}</span>
      )}
    </div>
  );
}

export default function ListingAd({ store }) {
  const {
    pic,
    title,
    address,
    description,
    psf_min,
    psf_max,
    subprice_label,
    availabilities_label,
    project_type,
    ownership_type,
    year,
  } = store;

  const [click, setClick] = useState(false);
  const [descriptionButton, setDescriptionButton] = useState('See description');

  const handleClick = () => {
    setClick(!click);
    setDescriptionButton(click ? 'See description' : 'Close description');
  };

  const per_square_foot = `$${psf_min.toLocaleString()} - $${psf_max.toLocaleString()} psf`;
  const properties_type = `${project_type} · ${year} · ${ownership_type}`;

  return (
    <div className="property-card">
      <div className="ribbon">Launching Soon</div>
      <div className="property-image-container">
        {/* ... (your existing code) */}
      </div>
      <div className="property-content">
        <div className="property-header">
          {/* ... (your existing code) */}
        </div>
        {click && (
          <div>
            <hr />
            <div className="description-styling">
              <CensoredPhoneNumber description={description} />
            </div>
          </div>
        )}
        <div className="button-container">
          <button
            id="button-text"
            onClick={handleClick}
            className="button-styling">
            {descriptionButton}
          </button>
        </div>
      </div>
    </div>
  );
}
